var Valida = require('..');


var schema = {
  number: [
    { validator: Valida.Validator.integer }
  ]
};


var data = { number: null };


Valida.process(data, schema, function(err, ctx) {
  if (err) return console.log(err);
  if (!ctx.isValid()) return console.log('invalid', ctx.errors());
  console.log('valid');
});


